mapper.select:
select

from e_chat_pet_personal_mission


    and chat_pet_id = #{chatPetId}


    and mission_code = #{missionCode}


    and state = #{state}


    and create_time >= #{createTime}


    AND ad_id = #{adId}

select

from e_chat_pet_personal_mission


    and chat_pet_id = #{chatPetId}


    and mission_code = #{missionCode}


    and state = #{state}


    and create_time >= #{createTime}


    and ad_id = #{adId}

