mapper.select:
select

from e_chat_pet_reward_item


    and chat_pet_id = #{chatPetId}


    and mission_item_id = #{missionItemId}


    and reward_state = #{rewardState}


    and create_time >= #{createTime}

mapper.update:
update e_chat_pet_reward_item
set gold_value = #{goldValue},reward_state = #{rewardState},mission_item_id = #{missionItemId} ,
chat_pet_id = #{chatPetId} , experience = #{experience} , create_time = #{createTime}
where id = #{id,jdbcType=INTEGER}
